var result = "Pornografi ";
var order = 10;
var table = {};

for(var i = 0; i < text.length; i++) {
  var substring = text.substring(i, i + order);
  var nextChar = text[i + order];
  if(!table[substring]) {
    table[substring] = [];
  }
  table[substring].push(nextChar);
}

function generate() {
  var next = result.substring(result.length - order);

  for(var i = 0; i < 1000; i++) {
    var possibilities = table[next];
    if(!possibilities) {
      break;
    } else {
      result += possibilities[Math.ceil(Math.random() * possibilities.length - 1)];
      next = result.substring(result.length - order);
    }
  }

  console.log(result);
}
